import Header from "../components/Header";
import Footer from '../components/Footer';
import { useState,useEffect } from 'react'
import { useParams } from 'react-router-dom';
import PropertyItem from "../components/PropertyItem";
import PropertyPageItem from "../components/PropertyPageItem";
const PropertyTypePage = () => {

    
// my state
const [property , setProperty] = useState({
    id:0,
    type:"",
    title:"",
    location:"",
    bestSeller:false,
    price: 0,
    Rules:"",
    amenities:"",
    img : null,
    
  });

  const {type} = useParams();

  useEffect(()=>{

    const URL =`http://localhost:5000/properties`
    // AJAX request

    fetch(URL)
    .then(response=>response.json())

    .then(json=>{
     

      setProperty(json)
    })
    .catch(err=>console.log(err))

  }, [])

    
  
    return(
        <div className="grid" id="container">
        <Header/>
        <main>
        {property.map((proper)=>( <PropertyPageItem id={proper.id} key={proper.id} type={proper.type} title={proper.title} bestSeller={proper.bestSeller} image ={proper.img} price={proper.price} location={proper.location} amenities={proper.amenities} rules={proper.Rules} />))}
            </main>
        <Footer/>
        </div>
    )
}

export default PropertyTypePage
