import Header from "../components/Header";
import Footer from '../components/Footer';
import { useState,useEffect } from 'react'
import { useParams } from 'react-router-dom';
import PropertyItem from "../components/PropertyItem";
import PropertyType from "../components/PropertyType";


const PropertyTypePage = () => {

    
// my state
const [properties , setProperties] = useState({
    id:0,
    type:"",
    title:"",
    location:"",
    bestSeller:false,
    price: 0,
    Rules:"",
    amenities:"",
    img : null,
    
  });

  const params = useParams()

  useEffect(()=>{

    const URL ="http://localhost:5000/properties"
    // AJAX request

    fetch(URL)
    .then(response=>response.json())

    .then(json=>{
     

      setProperties(json)
    })
    .catch(err=>console.log(err))

  }, [])

    
  
    return(
        <div className="grid" id="container">
        <Header/>
            <main>
            {properties.filter((property)=>{
                if(property.type === params.title){
                    return property;
                }else{
                    return null;
                }
            }).map((property)=>( <PropertyType id={property.id} key={property.id} type={property.type} title={property.title} bestSeller={property.bestSeller} image ={property.img} price={property.price} location={property.location} amenities={property.amenities} rules={property.Rules} />))}
            </main>
        <Footer/>
        </div>
    )
}

export default PropertyTypePage
