import Header from "../components/Header";
import Hero from '../components/Hero';
import PropertyType from '../components/PropertyType'
import BestSellerList from '../components/BestSellerList'
import Footer from '../components/Footer';

const HomePage = () => {
    // my state
  const [properties , setProperties] = useState([{
    id:"",
    type:"",
    title:"",
    location:"",
    bestSeller:false,
    price: "",
    imageSrc : null,
    rules:"",
    amenities:""
  }]);


  useEffect(()=>{

    const uri = 'http://localhost:8082/properties'
    // AJAX request

    fetch(uri)
    .then(response=>response.json())

    .then(json=>{
     

      setProperties(json)
    })
    .catch(err=>console.log(err))

  }, [])
    return(
        <div className="grid grid-row-3" >
        <Header id={property.id} key={property.id} type={property.type} title={property.title} bestSeller={property.bestSeller} image ={property.img} price={property.price} location={property.location} amenities={property.amenities} rules={property.rules} />
            <main>
                <Hero/>
                <PropertyType/>
                <BestSellerList/>
            </main>
            <Footer/>
        </div>
    )
}

export default HomePage
