import React,{useState,useEffect} from 'react';

import BestSellerItem from './BestSellerItem';

const PropertyPageList = () => {

  // my state
  const [properties , setProperties] = useState([{
    id:0,
    type:"",
    title:"",
    bestSeller:false,
    price: 0,
    imageSrc : null
  }]);


  useEffect(()=>{

    const URL = 'http://localhost:5000/properties'
    // AJAX request

    fetch(URL)
    .then(response=>response.json())

    .then(json=>{
     

      setProperties(json)
    })
    .catch(err=>console.log(err))

  }, [])
  return (
    <section >
      <div >
      
          <h1>Best Seller</h1>

          <div className="">
          {properties.filter((property)=>{
            return property.bestSeller===true;
        }).map(property=>( <BestSellerItem id={property.id} key={property.id} type={property.type} title={property.title} bestSeller={property.bestSeller} image ={property.img} price={property.price}  />))}
          
              
  
          </div>

      </div>

    </section>

  );
};

export default PropertyPageList;

<section id="property-listing-page">
  <div className='container'></div>
  

</section>
