        <Header/>
        <main>
        {properties.filter((property)=>{
            if(property.type === type){
                return property;
            }else{
                return null;
            }
        }).map((property)=>( <PropertyPageItem id={property.id} key={property.id} type={property.type} title={property.title} bestSeller={property.bestSeller} image ={property.img} price={property.price} location={property.location} amenities={property.amenities} rules={property.Rules} />))}
            </main>
        <Footer/>
        </div>
    )
}

